            new() { ID = 59, IsActive = true, LanguageCode = Language.Arabic.GetLanguageCode(), Name = DefaultEmailTemplate.IncorrectReturnClosedUIP.GetName(), Subject = "العنصر المرتجع بشكل غير صحيح يغلق السجل", Template = @"عزيزي الزميل،<br /> <br /> تم الإبلاغ عن العنصر '{{itemDescription}}' من النوع '{{itemTypeDescription}}' بشكل خاطئ كمرتجع من قبل المستخدم '{{userEmail}}' إلى IBK '{{cabinetName}}' في الموقع '{{positionAlias}}'<br /> <br /> تم تسجيل هذا الإجراء بإغلاق سجل الاستحواذ والعنصر '{{itemDescription}}' عاد إلى التداول. <br /> من الممكن أن يكون المستخدم '{{userEmail}}' قد تلقى العنصر '{{itemDescription}}' بشكل خاطئ ومن ثم أبلغ عنه كمرتجع خاطئ.<br /> لمنع المستخدم '{{userEmail}}' من وجود تسجيل خاطئ، يمكنك التحقيق في ذلك. <br /> <br /> اطلع على سجل المستخدم للمستخدم '{{userEmail}}' لمعرفة ما إذا كان لدى المستخدم عناصر مسجلة تحت حيازته.<br /> إذا كان لدى المستخدم '{{userEmail}}' عناصر مسجلة تحت حيازته، يمكنك التحقق من السجلات التشغيلية والفنية لمعرفة الإجراءات التي سبقت إشعار الإرجاع الخاطئ.<br /> إذا بدا أن المستخدم '{{userEmail}}' لديه تسجيل خاطئ، يرجى التواصل مع المستخدم '{{userEmail}}' للاستفسار عن أي عمليات سحب خاطئة.<br /> <br /> <b>لا ترد على هذا البريد الإلكتروني، فقد تم إرساله من عنوان 'لا يمكن الرد عليه'.</b>", CreateDT = DateTime.UtcNow, UpdateDT = DateTime.UtcNow },
